mapper.select:
select e.STORE_CODE, e.EMP_NO, EMP_NAME, EMP_ZIPCODE, EMP_ADDRESS, EMP_JUMIN, EMP_TEL, EMP_EMAIL, EMP_JOB, EMP_JOINDATE, EMP_OUTDATE, e.GRADE_CODE, e.DEPT_NO, d.DEPT_NAME, g.GRADE_NAME, s.STORE_NAME from EMPLOYEE e join DEPT d ON e.DEPT_NO = d.DEPT_NO join GRADE g ON e.GRADE_CODE = g.GRADE_CODE left outer join STORE s on e.STORE_CODE = s.STORE_CODE
e.store_code=#{storeCode}
and emp_name like '%' || #{empName} || '%'
and e.dept_No=#{deptNo}
and e.grade_code=#{gradeCode}
order by e.grade_code asc

mapper.update:
update employee
set DEPT_NO=#{deptNo},EMP_NAME=#{empName},EMP_PWD=#{empPwd},EMP_ZIPCODE=#{empZipcode},EMP_ADDRESS=#{empAddress},
	EMP_JUMIN=#{empJumin},EMP_TEL=#{empTel},EMP_FACE=#{empFace},EMP_JOB=#{empJob},GRADE_CODE=#{gradeCode}, EMP_EMAIL=#{empEmail}
	where EMP_NO=#{empNo}
